import { requestAccessToken } from "../Functions/SpotifyCalls";

export const verifyAccessToken = () => {
    let access_token = localStorage.getItme("ACCESS_TOKEN")
    access_token ? requestAccessToken().then(token => localStorage.setItem("ACCESS_TOKEN",token)) : null
}
export const SPOTIFY_API_URL = "https://api.spotify.com/v1/";
