export const mainStyle = (theme) => {
    return {
        backgroundColor: theme.background,
        color: theme.font.regular,
        width: '100vw',
        height: '100vh',
        padding: 0,
        margin: 0,
        justifyContent: 'center',
        textAlign: 'center'
    }
}

export const albumLayoutStyle = () => {
    return {
        width: '100vw',
        height: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItem: 'center',
        transform: 'skew(0,15deg)'
    }
}

export const albumImageStyle = () => {
    return {
        width: '300px',
        height: '300px',
        borderRadius: '10px',
    }
}

export const buttonStyle = (theme) => {
    return {
        background : 'none',
        border: 'none',
        color: '#fff',
        display: 'flex',
        justifyContent: 'center',
        alignItem: 'center'
    }
}
