export const mainStyle = (theme) => {
    return {
        backgroundColor: theme.background,
        color: theme.font.regular,
        width: '100vw',
        height: '100vh',
        padding: 0,
        margin: 0,
        justifyContent: 'center',
        textAlign: 'center'
    }
}

export const albumImageStyle = () => {
    return {
        width: '500px',
        height: '500px',
        borderRadius: '10px',
        border: '1px solid #fff'
    }
}
