export const requestAccessToken = () => {
    let formData = new FormData();
    formData.append("grant_type","client_credentials"); formData.append("client_id",process.env.CLIENT_ID); formData.append("client_secret",process.env.CLIENT_SECRET)
    fetch("https://accounts.spotify.com/api/token",{
        method: "POST",
        body: formData
    }).then((response) => {
        return response.json();
    })
}
fetch("",{
    method: "GET",
    header: `Authorization: Bearer ${"944aeebe158e4663bbc4feac0a1921f6"}`
})
