import { useState } from "react";

export default function PreliminaryInputComponent({theme,setCustomRange}) {
    const [inputText,setInputText] = useState(0);
    const [display,showDisplay] = useState(true)
    return (
        <div style = {{
            display: display ? 'flex': 'none',
            flexDirection: 'column',
            alignItems: 'center'
        }}>
            <h1>Enter any number from 0-100</h1>
            <input type = "number" onChange = {(e) => setInputText(e.target.value)} > 
            </input>
            <button 
            style = {{
                background: 'none',
                border: 'none',
                margin: '5px',
                color: theme.font.regular
            }} 
            onClick = {(e) => {
                setCustomRange(inputText)
                showDisplay(false)
                }}>Click to Begin</button>
        </div>
    )
}
