export const mainStyle = (theme) => {
    return {
        backgroundColor: theme.background,
        color: theme.font.link,
        width: '100vw',
        height: '100vh',
        padding: 0,
        margin: 0,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center'
    }
}
